import {FcDeleteDatabase} from "react-icons/fc"
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({item, itemIndex}) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  }

  return (
    <div>

      <div className="flex ml-20 mt-20">

        <div className="flex flex-col justify-center items-center " >
         <img src={item.image} className="h-[150px] w-[150px] " />
        </div>
        <div className="max-w-md">
          <h1 className="text-lg font-bold text-gray-800">{item.title}</h1> 
          <br></br>
          <h1 className="text-md text-gray-600">{item.description.split(" ").slice(0,10).join(" ") + "..."}</h1>
          <div className="flex justify-between relative">
            <p className="text-green-600 font-bold mt-10">${item.price}</p>
            <div className="absolute "
            onClick={removeFromCart}>
              <FcDeleteDatabase/>
            </div>
          </div>

        </div>


      </div>

    </div>
  );
};

export default CartItem;
